        loginButton.setText("Login");
        loginButton.addMouseListener(new java.awt.event.MouseAdapter() {
            public void mousePressed(java.awt.event.MouseEvent evt) {
            	//Handling
				if (usernameField.getText().isEmpty()) {
					// Please enter username
					showMessageDialog(null, "Please enter username");
					return;
				}
				if (passwordField.getText().isEmpty()) {
					showMessageDialog(null, "Please enter password");
					// Please enter password
					return;
				}

				String userName = usernameField.getText();
				String password = passwordField.getText();

				for (user user : Main.users) {
					if (user.getUserName().equals(userName) && user.getPassword().equals(password)) {
						loginButtonMousePressed(evt);					
						return;
					}
					if (user.getUserName().equals(userName)) {
						showMessageDialog(null, "Incorrect password. Please check your password again.");
						return;
					}
				}
				showMessageDialog(null,
						"Invalid username. Please check your username or click a register button below");
			}
        });
        registerButton.setText("Register");
        registerButton.addMouseListener(new java.awt.event.MouseAdapter() {
            public void mousePressed(java.awt.event.MouseEvent evt) {
                registerButtonMousePressed(evt);
            }
        });

