    /**
     * Function performed when the login button is pressed
     * Sets the active user, closes this panel, and opens main menu
     * @param evt Mouse event when clicked
     */
    private void loginButtonMousePressed(java.awt.event.MouseEvent evt) {
        // Check here if user is correct
        Main.setCurrentUser(usernameField.getText());
        Main.setCurrentUserObj();
        MainMenu mainFrame = new MainMenu();
        mainFrame.setVisible(true);
        dispose();
    }

    /**
     * Function performed when the register button is pressed
     * Closes this menu and opens register menu
     * @param evt Mouse event when clicked
     */
    private void registerButtonMousePressed(java.awt.event.MouseEvent evt) {
        Register registerFrame = new Register();
        registerFrame.setVisible(true);
        dispose();
    }

